import Vue from "vue";
import Router from "vue-router";
import Layout from "@/layout/Layout.vue";
import {
  getToken,
  setToken,
  removeToken,
  getStoreId,
  setStoreId,
  removeStoreId,
  setUserInfo,
  getUserInfo,
  removeUserInfo
} from "@/utils/cookies";
import store from "@/store";

Vue.use(Router);

const router = new Router({
  scrollBehavior: (to, from, savedPosition) => {
    if (savedPosition) {
      return savedPosition;
    }
    return { x: 0, y: 0 };
  },
  base: process.env.BASE_URL,
  routes: [
    {
      path: "/",
      component: () =>
        import(/* webpackChunkName: "login" */ "@/views/login/index.vue"),
      meta: { title: "苍穹外卖", hidden: true, notNeedAuth: true }
    },
    {
      path: "/404",
      component: () => import(/* webpackChunkName: "404" */ "@/views/404.vue"),
      meta: { title: "苍穹外卖", hidden: true, notNeedAuth: true }
    },
    //工区长
    {
      path: "/sectionLeader",
      component: Layout,
      redirect: "/sectionLeader/dashboard",
      meta: { title: "工区长", icon: "dashboard"},
      children: [
        {
          path: "dashboard",
          component: () =>
            import(/* webpackChunkName: "dashboard" */ "@/views/sectionLeader/dashboard/index.vue"),
          name: "sectionLeaderDashboard",
          meta: { title: '工作台', icon: 'dashboard', affix: true }
        },
        {
          path: "statistics",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/statistics/index.vue"),
          meta: {
            title: "数据统计",
            icon: "icon-statistics",
          }
        },
        {
          path: "purchase",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/purchase/index.vue"),
          meta: {
            title: "设备采购管理",
            icon: "icon-order",
          }
        },
        {
          path: "purchase/add",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/purchase/addPurchase.vue"),
          meta: {
            title: "添加采购任务",
            hidden: true,
          }
        },
        {
          path: "setmeal",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/setmeal/index.vue"),
          meta: {
            title: "设备出库申请审核",
            icon: "icon-combo",
          }
        },
        {
          path: "dish",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/dish/index.vue"),
          meta: {
            title: "设备审核管理管理",
            icon: "icon-dish",
          }
        },
        {
          path: "dish/add",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/dish/addDishtype.vue"),
          meta: {
            title: "添加菜品",
            hidden: true,
          }
        },
        {
          path: "employee",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/employee/index.vue"),
          meta: {
            title: "设备记录管理",
            icon: "icon-employee",
          }
        },

        {
          path: "/employee/add",
          component: () =>
            import(/* webpackChunkName: "dashboard" */ "@/views/sectionLeader/employee/addEmployee.vue"),
          meta: {
            title: "添加员工",
            hidden: true,
          }
        },
        

        {
          path: "setmeal/add",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/setmeal/addSetmeal.vue"),
          meta: {
            title: "添加套餐",
            hidden: true,
          }
        }
      ]
    },
    //采购管理员
    {
      path: "/purchasingOffer",
      component: Layout,
      redirect: "/purchasingOffer/dashboard",
      children: [
        {
          path: "dashboard",
          component: () =>
            import(/* webpackChunkName: "dashboard" */ "@/views/purchasingOffer/dashboard/index.vue"),
          name: "purchasingOfferdashboard",
          meta: {
            title: "工作台",
            icon: "dashboard",
            affix: true,
           
          }
        },
        {
          path: "statistics",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/purchasingOffer/statistics/index.vue"),
          meta: {
            title: "数据统计",
            icon: "icon-statistics",
          
          }
        },
        {
          path: "purchase",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/purchasingOffer/purchase/index.vue"),
          meta: {
            title: "设备采购管理",
            icon: "icon-order",
           
          }
        },

      ]
    },


    {
      path: "*",
      redirect: "/404",
      meta: { hidden: true }
    }
  ]
});

export default router;
