import { Component, Vue } from 'vue-property-decorator';
import { getDashboardData, getChartData } from '@/api/charts';
import LineChart from './components/LineChart.vue';
import PieChart from './components/PieChart.vue';

@Component({
  name: 'Dashboard',
  components: {
    LineChart,
    PieChart
  }
})
export default class extends Vue {
  private statistics = {
    totalDevices: 0,
    inStockDevices: 0,
    todayOutbound: 0,
    pendingFault: 0
  };

  private chartDateRange = [];
  private lineChartData = {
    labels: [],
    datasets: [
      {
        label: '入库数量',
        data: [],
        backgroundColor: '#409EFF',
        borderColor: '#409EFF'
      },
      {
        label: '出库数量',
        data: [],
        backgroundColor: '#67C23A',
        borderColor: '#67C23A'
      }
    ]
  };

  private pieChartData = {
    labels: [],
    datasets: [
      {
        data: [],
        backgroundColor: [
          '#409EFF',
          '#67C23A',
          '#E6A23C',
          '#F56C6C'
        ]
      }
    ]
  };

  private recentInbound = [];
  private pendingOutbound = [];

  created() {
    this.loadData();
    this.loadChartData();
  }

  private async loadData() {
    try {
      const res = await getDashboardData();
      if (res.data.code === 1) {
        this.statistics = res.data.data.statistics;
        this.recentInbound = res.data.data.recentInbound;
        this.pendingOutbound = res.data.data.pendingOutbound;
      }
    } catch (err) {
      this.$message.error('加载数据失败: ' + err.message);
    }
  }

  private async loadChartData() {
    try {
      const params = {
        startDate: this.chartDateRange && this.chartDateRange[0],
        endDate: this.chartDateRange && this.chartDateRange[1]
      };
      
      const res = await getChartData(params);
      if (res.data.code === 1) {
        this.lineChartData = {
          labels: res.data.data.lineChart.labels,
          datasets: [
            {
              label: '入库数量',
              data: res.data.data.lineChart.inboundData,
              backgroundColor: '#409EFF',
              borderColor: '#409EFF'
            },
            {
              label: '出库数量',
              data: res.data.data.lineChart.outboundData,
              backgroundColor: '#67C23A',
              borderColor: '#67C23A'
            }
          ]
        };

        this.pieChartData = {
          labels: res.data.data.pieChart.labels,
          datasets: [
            {
              data: res.data.data.pieChart.data,
              backgroundColor: [
                '#409EFF',
                '#67C23A',
                '#E6A23C',
                '#F56C6C'
              ]
            }
          ]
        };
      }
    } catch (err) {
      this.$message.error('加载图表数据失败: ' + err.message);
    }
  }

  private getEquipmentTypeName(type: number) {
    const types = {
      1: '客服设备',
      2: '机电设备',
      3: '消防设备',
      4: '电梯设备'
    };
    return types[type] || '未知类型';
  }

  private goToInbound() {
    this.$router.push(`/WarehouseManager/outbound`);
  }

  private goToOutbound() {
    this.$router.push(`/WarehouseManager/inbound`);
  }
}
