import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import ImageUpload from '@/components/ImgUpload/index.vue'
import { submitPurchase } from '@/api/purchase'

@Component({
  name: 'addShop',
  components: {
    HeadLable,
    ImageUpload
  }
})
export default class extends Vue {
  private value: string = ''
  private actionType: string = ''
  private ruleForm = {
    equipmentName: '',
    equipmentType: '',
    modelNumber: '',
    specification: '',
    manufacturer: '',
    supplier: '',
    quantity: '',
    unit: '',
    warrantyPeriod: '',
    expectedLife: '',
    remark: '',
  }
  get rules() {
    return {
      equipmentName: {
        required: true,
        validator: (rule: any, value: string, callback: Function) => {
          if (!value) {
            callback(new Error('请输入名称'));
          } else {
            const reg = /^([A-Za-z0-9\u4e00-\u9fa5]){2,20}$/;
            if (!reg.test(value)) {
              callback(new Error('名称输入不符，请输入2-20个字符'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      equipmentType: {
        required: true,
        message: '请选择类型',
        trigger: 'change'
      },
      modelNumber: {
        required: false,
        message: '请输入型号',
        trigger: 'blur'
      },
      specification: {
        required: false,
        message: '请输入规格',
        trigger: 'blur'
      },
      manufacturer: {
        required: false,
        message: '请输入制造商',
        trigger: 'blur'
      },
      supplier: {
        required: false,
        message: '请输入供应商',
        trigger: 'blur'
      },
      quantity: {
        required: true,
        validator: (rule: any, value: string, callback: Function) => {
          if (!value) {
            callback(new Error('请输入数量'));
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('数量必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      unit: {
        required: false,
        message: '请输入单位',
        trigger: 'blur'
      },
      warrantyPeriod: {
        required: false,
        validator: (rule: any, value: string, callback: Function) => {
          // 如果 value 为空，则直接通过校验
          if (!value) {
            callback();
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('保修期必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },

      expectedLife: {
        required: false,
        validator: (rule: any, value: string, callback: Function) => {
          // 如果 value 为空，则直接通过校验
          if (!value) {
            callback();
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('预期寿命必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      remark: {
        required: false, // 备注可以是非必填
        validator: (rule: any, value: string, callback: Function) => {
          if (value && value.length > 100) {
            callback(new Error('备注不能超过100个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      }
    }
  }

  created() {
    this.getDishTypeList()
    this.actionType = this.$route.query.id ? 'edit' : 'add'
    if (this.actionType == 'edit') {
      this.init()
    }
  }

  private async init() {

  }
  private seachHandle() {

  }
  // 获取套餐分类
  private getDishTypeList() {

  }  // 删除套餐菜品
  delDishHandle(index: any) {

  }

  // 获取添加菜品数据 - 确定加菜倒序展示
  private getCheckList(value: any) {

  }

  public submitForm(formName: any, st: any) {
    ; (this.$refs[formName] as any).validate((valid: any) => {
      if (valid) {
        let prams = { ...this.ruleForm } as any
        submitPurchase(prams)
          .then(res => {
            if (res && res.data && res.data.code === 1) {
              this.$message.success('任务下发成功！')
              this.$router.push({ path: '/sectionLeader/purchase' })
              if (!st) {
                this.$router.push({ path: '/sectionLeader/purchase' })
              } else {
                ; (this as any).$refs.ruleForm.resetFields()
                this.ruleForm = {
                  equipmentName: '',
                  equipmentType: '',
                  modelNumber: '',
                  specification: '',
                  manufacturer: '',
                  supplier: '',
                  quantity: '',
                  unit: '',
                  warrantyPeriod: '',
                  expectedLife: '',
                  remark: '',
                } as any
              }
            } else {
              this.$message.error(res.data.msg)
            }
          })
          .catch(err => {
            this.$message.error('请求出错了：' + err.message)
          })
      } else {

        // 如果所有手动校验都通过，但仍然 valid 为 false，可能是其他字段的问题

        return this.$message.error('表单校验失败，请检查输入内容');
      }
    })
  }
}
