import { Component, Vue } from 'vue-property-decorator'
// 删除或注释掉不再使用的导入
// import { getBusinessData, getOrderData } from '@/api/index'
// import { getDeviceList,getEmployeeStats,getRecentEmployees} from '@/api/device'
// import { getOrderDetailPage } from '@/api/purchase'
import { pastMonth } from '@/utils/formValidate'
// 导入新创建的API接口
import { getMonthlyDashboardData, getTaskStatistics, getWarehouseInventory, getEmployeeStatistics, getRecentEmployees,getDevice } from '@/api/dashboard'
@Component({
  name: 'Dashboard'
})
export default class extends Vue {
private overviewData = {
    deviceCount: 0,
    pendingTasks: 0,
    taskCompletionRate: 0,
    purchaseCount: 0,
    employeeCount: 0
  }

  private taskData = {
    maintenanceTasks: 0,
    inspectionTasks: 0,
    testingTasks: 0,
    faultTasks: 0
  }

  private deviceData = {
    normalCount: 0,
    repairingCount: 0,
    disabledCount: 0,
    normalRate: 0,
    repairingRate: 0,
    layRate: 0,
    typeCounts: {
      客服设备: 0,
      机电设备: 0,
      消防设备: 0,
      电梯设备: 0
    },
    typeRates: {
      客服设备: 0,
      机电设备: 0,
      消防设备: 0,
      电梯设备: 0
    }
  }

  private employeeData = {
    roleCounts: {
      普通员工: 0,
      班组长: 0,
      工程师: 0,
      采购人员: 0,
      工区长: 0,
      仓库管理员: 0

    },
    roleRates: {
      普通员工: 0,
      班组长: 0,
      工程师: 0,
      采购人员: 0,
      工区长: 0,
      仓库管理员: 0
    },
    recentEmployees: []
  }

  private purchaseData = []

  get days() {
    return pastMonth()
  }

  created() {
    this.init()
  }

  init() {
    this.$nextTick(() => {
      this.getOverviewData()
      this.getTaskData()
      this.getDeviceData()
      this.getEmployeeData()
    })
  }

  // 获取概览数据
  async getOverviewData() {
    try {
      const response = await getMonthlyDashboardData()
      if (response.data.code === 1) {
        this.overviewData = response.data.data
      }
    } catch (error) {
      console.error('获取概览数据失败', error)
    }
  }

  // 获取任务数据
  async getTaskData() {
    try {
      const response = await getTaskStatistics()
      if (response.data.code === 1) {
        this.taskData = response.data.data
      }
    } catch (error) {
      console.error('获取任务数据失败', error)
    }
  }

  // 获取设备数据
  async getDeviceData() {
    try {
      const params = {
        page: 1,
        pageSize: 1000
      }
      const data = await getDevice()
      if (data.data.code === 1) {
        const devices = data.data.data || []
        const totalCount = devices.length

        // 计算设备状态数量
        const normalCount = devices.filter(d => d.status === 1).length
        const repairingCount = devices.filter(d => d.status === 2).length
        const disabledCount = devices.filter(d => d.status === 3).length

        // 计算设备类型数量
        const typeCount = {
          客服设备: devices.filter(d => d.deviceType === '客服设备').length,
          机电设备: devices.filter(d => d.deviceType === '机电设备').length,
          消防设备: devices.filter(d => d.deviceType === '消防设备').length,
          电梯设备: devices.filter(d => d.deviceType === '电梯设备').length
        }

        this.deviceData = {
          normalCount,
          repairingCount,
          disabledCount,
          normalRate: totalCount > 0 ? normalCount / totalCount : 0,
          repairingRate: totalCount > 0 ? repairingCount / totalCount : 0,
          layRate: totalCount > 0 ? disabledCount / totalCount : 0,
          typeCounts: typeCount,
          typeRates: {
            客服设备: totalCount > 0 ? typeCount.客服设备 / totalCount : 0,
            机电设备: totalCount > 0 ? typeCount.机电设备 / totalCount : 0,
            消防设备: totalCount > 0 ? typeCount.消防设备 / totalCount : 0,
            电梯设备: totalCount > 0 ? typeCount.电梯设备 / totalCount : 0
          }
        }
      }
    } catch (error) {
      console.error('获取设备数据失败', error)
    }
  }


  // 获取设备类型文本
  getEquipmentType(type: string) {
    const typeMap = {
      '1': '客服设备',
      '2': '机电设备',
      '3': '消防设备',
      '4': '电梯设备'
    }
    return typeMap[type] || '未知类型'
  }

  // 获取员工数据
  async getEmployeeData() {
  try {
    // 获取员工统计数据
    const statsRes = await getEmployeeStatistics()
    // 获取最近5名员工
    const recentRes = await getRecentEmployees({ pageSize: 5 })
    
    if (statsRes.data.code === 1 && recentRes.data.code === 1) {
      const stats = statsRes.data.data
      const recentEmployees = recentRes.data.data.records || []
      
      // 角色ID到中文名称的映射
      const roleIdToName = {
        1: '普通员工',
        2: '班组长',
        3: '工区长',
        4: '工程师',
        5: '采购人员',
        6: '仓库管理员'
      }
      
      // 角色英文键名到中文名称的映射
      const roleKeyToName = {
        'commonStaff': '普通员工',
        'teamLeader': '班组长',
        'areaManager': '工区长',
        'engineer': '工程师',
        'purchaser': '采购人员',
        'warehouseAdmin': '仓库管理员'
      }
      
      // 转换角色计数数据结构
      const roleCounts = {
        '普通员工': stats.roleCounts.commonStaff || 0,
        '班组长': stats.roleCounts.teamLeader || 0,
        '工区长': stats.roleCounts.areaManager || 0,
        '工程师': stats.roleCounts.engineer || 0,
        '采购人员': stats.roleCounts.purchaser || 0,
        '仓库管理员': stats.roleCounts.warehouseAdmin || 0
      }
      
      // 计算各职位比例
      const totalEmployees = Object.values(roleCounts).reduce((a, b) => a + b, 0)
      const roleRates = {} as any
      
      Object.keys(roleCounts).forEach(role => {
        roleRates[role] = totalEmployees > 0 ? roleCounts[role] / totalEmployees : 0
      })
      
      this.employeeData = {
        roleCounts,
        roleRates,
        recentEmployees: recentEmployees.map((emp: any) => ({
          realName: emp.realName,
          roleName: roleIdToName[emp.roleId] || '未知角色',  // 使用roleId映射到角色名称
          createTime: emp.createTime
        }))
      }
    }
  } catch (error) {
    console.error('获取员工数据失败', error)
  }
}



