import  request from '@/utils/request';


// 新增故障任务
export const submitFaultTask = (params: any) => {
  return request({
    url: '/team_leader/dispatch_fault_task',
    method: 'post',
    data: { ...params }
  })
}


// 获取设备列表
export const getDeviceList = () =>
  request({
    url: '/team_leader/getDeviceId',
    method: 'get',
  })

export const submitInspectionTask = (params: any) => {
  return request({
    url: '/team_leader/dispatch_inspection_task',
    method: 'post',
    data: { ...params }
  })
}

export const submitDetectionTask = (params: any) => {
  return request({
    url: '/team_leader/dispatch_detection_task',
    method: 'post',
    data: { ...params }
  })
}

export const submitMaintenanceTask = (params: any) => {
  return request({
    url: '/team_leader/dispatch_maintenance_task',
    method: 'post',
    data: { ...params }
  })
}



// 获取故障列表（分页）
export const getFaultDetailPage = (params: any) =>
  request({
    url: '/team_leader/pending_verify',
    method: 'get',
    params
  });


// 根据故障编号查询故障详情
export const queryFaultDetailById = (faultId: string) =>
  request({
    url: `/team_leader/fault_detail/${faultId}`,
    method: 'get'
  });

//根据故障ID查询任务
export const getTaskInfoByFaultId = (faultId: string) =>
  request({
    url: `/team_leader/fault_detail/${faultId}`,
    method: 'get'
  });
// 获取各状态故障数量
export const getFaultStatusListBy = (params: any) =>
  request({
    url: '/fault/statusCount',
    method: 'get',
    params
  });

// 班组长审核
export const auditFault = (params: any) =>
  request({
    url: '/team_leader/verify_fault',
    method: 'post',
    data: params
  });


  
// 工区长审核
export const auditFault2 = (params: any) =>
  request({
    url: '/area_leader/verify_fault',
    method: 'post',
    data: params
  });

  
// 获取故障销号审核列表
export const getFaultVerifyList = (params: any) =>
  request({
    url: '/it_manager/pending_sign',
    method: 'get',
    params
  })

// 提交故障销号审核
export const submitFaultVerify = (data: any) =>
  request({
    url: '/it_manager/sign_fault',
    method: 'post',
    data
  })

  // 故障销号审核详情
  export function queryFaultVerifyDetailById(faultId: string) {
    return request({
      url: `/it_manager/fault_verify/${faultId}`,
      method: 'get',
      params: { faultId }
    })
  }

// 获取工程师列表
export const getEngineerList = () =>
   request({
     url: '/team_leader/getAvailableEngineers',
     method: 'get',
 })
