import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import InputAutoComplete from '@/components/InputAutoComplete/index.vue'
import TabChange from './tabChange.vue'
import Empty from '@/components/Empty/index.vue'
import {
  getOrderDetailPage,
  queryPurchaseDetailById,
  purchaseCancel,
  auditPurchase,
  getPurchaseListBy,
} from '@/api/purchase'
import { status } from 'nprogress'

@Component({
  components: {
    HeadLable,
    InputAutoComplete,
    TabChange,
    Empty,
  },
})
export default class extends Vue {
  private defaultActivity: any = 0
  private orderStatics = {}
  private row = {}
  private isAutoNext = true
  private isTableOperateBtn = true
  private currentPageIndex = 0 //记录查看详情数据的index
  private equipmentType = ''
  private purchaseId = '' //采购单号
  private valueTime = []
  private dialogVisible = false //详情弹窗
  private cancelDialogVisible = false //取消，拒单弹窗
  private cancelDialogTitle = '取消' //默认取消
  private cancelReason = ''
  private refuseReason = ''
  private remark = '' //自定义原因
  private equipmentName = ''
  private counts: number = 0
  private page: number = 1
  private pageSize: number = 10
  private tableData = []
  private cancelPurchaseList = [
    {
      label: '重复采购',
      value: '重复采购',
    },
    {
      label: '订单重复',
      value: '订单重复',
    },
    {
      label: '项目取消',
      value: '项目取消',
    },
    {
      label: '订单内容填写错误',
      value: '订单内容填写错误',
    },
    {
      label: '自定义原因',
      value: '自定义原因',
    },
  ]
  private refusePurchaseList = [
    {
        label: '不符合采购要求',
        value: '不符合采购要求',
    },
    {
        label: '预算超支',
        value: '预算超支',
    },
    {
        label: '供应商资质不符',
        value: '供应商资质不符',
    },
    {
        label: '采购流程不合规',
        value: '采购流程不合规',
    },
    {
        label: '自定义原因',
        value: '自定义原因',
    }
];

  private diaForm = []
  private isSearch: boolean = false
  private purchaseStatus = -1 //列表字段展示所需订单状态,用于分页请求数据
  private dialogPurchaseStatus = 0 //弹窗所需订单状态，用于详情展示字段

  private purchaseList = [
    {
      label: '全部采购单',
      value: -1,
    },
    {
      label: '待接收',
      value: 0,
    },
    {
      label: '进行中',
      value: 1,
    },
    {
      label: '待审核',
      value: 2,
    },
    {
      label: '待采购',
      value: 3,
    },
    {
      label: '已完成',
      value: 4,
    },
    {
      label: '已驳回',
      value: 5,
    },
    {
      label: '已取消',
      value: 6,
    },
  ]

  created() {
    this.init(Number(this.$route.query.status) || -1)
  }

  mounted() {
    //如果有值说明是消息通知点击进来的
    if (
      this.$route.query.orderId &&
      this.$route.query.orderId !== 'undefined'
    ) {
      this.goDetail(this.$route.query.orderId, 2)
    }
    if (this.$route.query.status) {
      this.defaultActivity = this.$route.query.status
    }
    // console.log(this.$route.query, 'this.$route')
  }

  initFun(purchaseStatus) {
    this.page = 1
    this.init(purchaseStatus)
  }

  //管理标签页点击事件
  change(activeIndex) {
    if (activeIndex === this.purchaseStatus) return
    this.init(activeIndex)
    this.valueTime = []
    this.dialogPurchaseStatus = 0
    this.$router.push('/sectionLeader/purchase')
    console.log(activeIndex, '接收到了子组件的index')
  }

  //获取待处理，待派送，派送中数量
  getOrderListBy4Status() {
    getPurchaseListBy({})
      .then((res) => {
        if (res.data.code === 1) {
          this.orderStatics = res.data.data
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }

  init(activeIndex: number = -1, isSearch?) {
    this.isSearch = isSearch
    const params = {
      page: this.page,
      pageSize: this.pageSize,
      equipmentName: this.equipmentName || undefined,
      equipmentType: this.equipmentType || undefined,
      beginTime:
        this.valueTime && this.valueTime.length > 0
          ? this.valueTime[0]
          : undefined,
      endTime:
        this.valueTime && this.valueTime.length > 0
          ? this.valueTime[1]
          : undefined,
      status: activeIndex === -1 ? undefined : activeIndex,
      // isDeleted:activeIndex === 5 ? 1 : 0
    }
    getOrderDetailPage({ ...params })
      .then((res) => {
        if (res.data.code === 1) {
          console.log(this.purchaseStatus)
          this.tableData = res.data.data.records
          this.purchaseStatus = activeIndex
          console.log(activeIndex)
          this.counts = Number(res.data.data.total)
          this.getOrderListBy4Status()
          //处理下一条操作
          /* if (
            this.dialogOrderStatus === 2 &&
            this.purchaseStatus === 2 &&
//           this.isAutoNext &&
//           !this.isTableOperateBtn &&
            res.data.data.records.length > 1
          ) {
            const row = res.data.data.records[0]
            this.goDetail(row.id, row.status, row)
          } else {
            return null
          } */
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }

  // 添加更改
  private sendPurchaseInfo(st: any) {
    if (st === 'add') {
      this.$router.push({ path: '/sectionLeader/purchase/add' })
    } else {
      this.$router.push({ path: '/sectionLeader/purchase/add', query: { id: st.id } })
    }
  }

  //获取采购状态
  getPurchaseType(row: any) {
    if (row.status === 0) {
      return '待接收'
    } else if (row.status === 1) {
      return '进行中'
    } else if (row.status === 2) {
      return '待审核'
    } else if (row.status === 3) {
      return '待采购'
    } else if (row.status === 4) {
      return '已完成'
    } else if (row.status === 5) {
      return '已取消'
    } else {
      return '状态错误'
    }
  }

  //获取设备类型
  getEquimentType(row: any) {
    if (row.equipmentType === 1) {
      return '客服设备'
    } else if (row.equipmentType === 2) {
      return '机电设备'
    } else if (row.equipmentType === 3) {
      return '消防设备'
    } else if (row.equipmentType === 4) {
      return '电梯设备'
    } else {
      return '状态错误'
    }
  }


  // 查看详情
  async goDetail(id: any, status: number, row?: any) {
    // console.log(111, index, row)
    this.diaForm = []
    this.dialogVisible = true
    this.dialogPurchaseStatus = status
    this.purchaseId = id
    console.log("查看详情Id" + id)
    const { data } = await queryPurchaseDetailById({ purchaseId: id })
    this.diaForm = data.data
    this.row = row || { id: this.$route.query.purchaseId, status: status }
    if (this.$route.query.purchaseId) {
      this.$router.push('/purchase')
    }
  }

  //审核通过
  passPurchase(row: any) {
    this.purchaseId = row.purchaseId
    this.dialogPurchaseStatus = row.status
    auditPurchase({
      purchaseId: this.purchaseId,
      status: 3,
      remark: '审核通过'
    })
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success("审核通过！")
          this.purchaseId = ''
          // this.dialogOrderStatus = 0
          this.dialogVisible = false
          this.init(this.purchaseStatus)
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }
  //审核不通过
  refusePurchase(row: any) {
    this.cancelDialogVisible = true
    this.purchaseId = row.purchaseId
    this.dialogPurchaseStatus = row.status
    this.dialogVisible = false
    this.cancelDialogTitle = '审核不通过'
    this.refuseReason = ''

  }

  //打开取消订单弹窗
  cancelPurchase(row: any) {
    this.cancelDialogVisible = true
    this.purchaseId = row.purchaseId
    this.dialogVisible = false
    this.cancelReason = ''
    this.cancelDialogTitle = '取消'
    this.dialogPurchaseStatus = row.status
  }


  //确认取消或拒绝订单并填写原因
  confirmCancel(type) {
    if (!this.cancelReason&&!this.refuseReason) {
      return this.$message.error(`请选择${this.cancelDialogTitle}原因`)
    } 

    ;(this.cancelDialogTitle === '取消' ? purchaseCancel : auditPurchase)({
      purchaseId: this.purchaseId,
      status:this.cancelDialogTitle === '取消' ? 6 : 5,
      // eslint-disable-next-line standard/computed-property-even-spacing
      [this.cancelDialogTitle === '取消' ? 'cancelReason' : 'refuseReason']: 
      this.cancelDialogTitle === '取消' ? this.cancelReason : this.refuseReason
    })
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success('操作成功')
          this.cancelDialogVisible = false
          this.purchaseId = ''
          // this.dialogOrderStatus = 0
          this.init(this.purchaseStatus)
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }

  handleClose() {
    this.dialogVisible = false
  }

  private handleSizeChange(val: any) {
    this.pageSize = val
    this.init(this.purchaseStatus)
  }

  private handleCurrentChange(val: any) {
    this.page = val
    this.init(this.purchaseStatus)
  }
}
