import  request  from '@/utils/request';

// 获取检测记录分页列表
export const getDetectionPage = (params: any) => {
  return request({
    url: '/team_leader/pending_detection_verify',
    method: 'get',
    params
  });
};

// 根据ID查询检测任务详情
export const queryDetectionTaskById = (detectionId: string) => {
  return request({
    url: `/detection/detection_task/${detectionId}`,
    method: 'get'
  });
};

// 根据检测编号查询检测详情
export const queryDetectionDetailById = (detectionId: string) =>
  request({
    url: `/it_manager/detection_detail/${detectionId}`,
    method: 'get'
  });



// 获取各状态检测数量
export const getDetectionStatusListBy = (params: any) =>
  request({
    url: '/fault/statusCount',
    method: 'get',
    params
  });



// 班组长审核
export const auditDetection = (params: any) =>
  request({
    url: '/team_leader/verify_detection',
    method: 'post',
    data: params
  });

  
// 工区长审核
export const auditDetection2 = (params: any) =>
  request({
    url: '/area_leader/verify_detection',
    method: 'post',
    data: params
  });


// 获取检测验收列表
export const getDetectionVerifyList = (params: any) =>
  request({
    url: '/it_manager/pending_detection_sign',
    method: 'get',
    params
  })

// 提交电子签名
export const submitDetectionVerify = (data: any) =>
  request({
    url: '/it_manager/sign_detection',
    method: 'post',
    data
  })



  // 检测验收审核详情
  export function queryDetectionVerifyDetailById(detectionId: string) {
    return request({
      url: `/it_manager/detection_verify/${detectionId}`,
      method: 'get',
      params: { detectionId }
    })
  }
