import { Component, Vue } from 'vue-property-decorator'
import { getInformData, batchMsg, setStatus } from '@/api/inform'
import Empty from '@/components/Empty/index.vue'

@Component({
  name: 'Inform',
  components: {
    Empty
  }
})
export default class extends Vue {
  private tableData: any[] = []
  private messageType = ''
  private status = ''
  private page = 1
  private pageSize = 10
  private counts = 0
  private isSearch = false

  // 消息类型列表
  private messageTypeList = [
    {
      value: 1,
      label: '待审核'
    },
    {
      value: 2,
      label: '催单'
    },
    {
      value: 3,
      label: '故障报修'
    },
    {
      value: 4,
      label: '维护保养'
    },
    {
      value: 5,
      label: '巡检验收'
    },
    {
      value: 6,
      label: '检测验收'
    }
  ]

  // 状态列表
  private statusList = [
    {
      value: 0,
      label: '未读'
    },
    {
      value: 1,
      label: '已读'
    }
  ]

  created() {
    this.init()
  }

  // 初始化获取数据
  private init() {
    this.getList()
  }

  // 获取消息列表
  private getList() {
    this.isSearch = true
    getInformData({
      page: this.page,
      pageSize: this.pageSize,
      type: this.messageType,
      status: this.status
    })
      .then(res => {
        if (res.data.code === 1) {
          this.tableData = res.data.data.records || []
          this.counts = res.data.data.total
        } else {
          this.$message.error(res.data.msg || '获取消息列表失败')
        }
      })
      .catch(() => {
        this.$message.error('未知错误请联系管理员')
      })
  }

  // 获取消息类型文本
  private getMessageType(row: any) {
    const item = this.messageTypeList.find(item => item.value === row.type)
    return item ? item.label : '未知类型'
  }

  // 获取状态文本
  private getStatus(row: any) {
    const item = this.statusList.find(item => item.value === row.status)
    return item ? item.label : '未知状态'
  }

  // 标记已读
  private markRead(row: any) {
    setStatus(row.messageId)
      .then(res => {
        if (res.data.code === 1) {
          this.$message.success('标记已读成功')
          this.getList()
        } else {
          this.$message.error(res.data.msg || '操作失败')
        }
      })
      .catch(() => {
        this.$message.error('未知错误请联系管理员')
      })
  }

  // 全部标记已读
  private markAllRead() {
    batchMsg({})
      .then(res => {
        if (res.data.code === 1) {
          this.$message.success('全部标记已读成功')
          this.getList()
        } else {
          this.$message.error(res.data.msg || '操作失败')
        }
      })
      .catch(() => {
        this.$message.error('未知错误请联系管理员')
      })
  }

  // 处理消息
  private handleMessage(row: any) {
    // 根据消息类型跳转到不同页面
    switch (row.type) {
      case 1: // 待审核
        if (row.faultId) {
          this.$router.push(`/sectionLeader/deviceReview/faultClear?faultId=${row.faultId}`)
        } else if (row.maintenanceId) {
          this.$router.push(`/sectionLeader/deviceReview/maintenanceAcceptance?maintenanceId=${row.maintenanceId}`)
        } else if (row.inspectionId) {
          this.$router.push(`/sectionLeader/deviceReview/inspectionAcceptance?inspectionId=${row.inspectionId}`)
        } else if (row.detectionId) {
          this.$router.push(`/sectionLeader/deviceReview/testingAcceptance?detectionId=${row.detectionId}`)
        }
        break
      case 2: // 催单
        if (row.purchaseId) {
          this.$router.push(`/sectionLeader/purchase?purchaseId=${row.purchaseId}`)
        } else if (row.outboundId) {
          this.$router.push(`/sectionLeader/outbound?outboundId=${row.outboundId}`)
        }
        break
      case 3: // 故障报修
        this.$router.push(`/sectionLeader/deviceReview/faultClear?faultId=${row.faultId}`)
        break
      case 4: // 维护保养
        this.$router.push(`/sectionLeader/deviceReview/maintenanceAcceptance?maintenanceId=${row.maintenanceId}`)
        break
      case 5: // 巡检验收
        this.$router.push(`/sectionLeader/deviceReview/inspectionAcceptance?inspectionId=${row.inspectionId}`)
        break
      case 6: // 检测验收
        this.$router.push(`/sectionLeader/deviceReview/testingAcceptance?detectionId=${row.detectionId}`)
        break
      default:
        this.$message.warning('无法处理该类型消息')
    }

    // 如果消息未读，标记为已读
    if (row.status === 0) {
      this.markRead(row)
    }
  }

  // 分页相关方法
  private handleSizeChange(val: number) {
    this.pageSize = val
    this.init()
  }

  private handleCurrentChange(val: number) {
    this.page = val
    this.init()
  }
}
