import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import ImageUpload from '@/components/ImgUpload/index.vue'
import { confirmPurchasingForm, queryPurchaseDetailById, submitPurchasingForm } from '@/api/purchase'
import { status } from 'nprogress'
import { number } from 'echarts'
import { getToken } from '@/utils/cookies'


@Component({
  name: 'addShop',
  components: {
    HeadLable,
    ImageUpload
  }
})
export default class extends Vue {
  private value: string = ''
  private purchaseId: string = ''
  private status: string = ''
  private initiallyNonEmptyFields: string[] = []; // 存储初始非空字段名
  private ruleForm = {
    purchaseId: '', // 采购ID
    equipmentType: '', // 设备类型
    equipmentName: '', // 设备名称
    modelNumber: '', // 型号
    specification: '', // 规格参数
    manufacturer: '', // 生产厂家
    supplier: '', // 供应商
    purchaseDate: '', // 采购日期
    purchaseAmount: '', // 采购金额
    quantity: '', // 采购数量
    unit: '', // 单位
    warrantyPeriod: '', // 保修期(月)
    expectedLife: '', // 预期使用寿命(月)
    purchaseDocumentNo: '', // 采购单据编号
    contractNo: '', // 合同编号
    purchasePerson: '', // 采购负责人
    image: '', // 采购设备图像
    status: 0,
    deliveryDate: '', // 交付日期
    acceptanceDate: '', // 验收日期
    acceptanceResult: '', // 验收结果
    acceptanceNotes: '', // 验收备注
    remark: '', // 备注
    cancelReason: '', // 取消采购单原因
    refuseReason: '', // 审核拒绝原因
    promptMessage: '', // 催促通知
    createTime: '', // 创建时间
    createBy: '', // 创建人
  }
  private headers = {
  token: getToken()
}
  get rules() {
    return {
      equipmentName: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback(new Error('请输入设备名称'));
          } else {
            const reg = /^([A-Za-z0-9\u4e00-\u9fa5]){2,20}$/;
            if (!reg.test(value)) {
              callback(new Error('设备名称输入不符，请输入2-20个字符'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      equipmentType: {
        required: true,
        message: '请选择设备类型',
        trigger: 'change'
      },
      modelNumber: {
        required: true,
        message: '请输入设备型号',
        trigger: 'blur'
      },
      specification: {
        required: true,
        message: '请输入规格参数',
        trigger: 'blur'
      },
      manufacturer: {
        required: true,
        message: '请输入生产厂家',
        trigger: 'blur'
      },
      supplier: {
        required: true,
        message: '请输入供应商',
        trigger: 'blur'
      },
      quantity: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback(new Error('请输入采购数量'));
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('采购数量必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      unit: {
        required: true,
        message: '请输入单位',
        trigger: 'blur'
      },
      warrantyPeriod: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback();
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('保修期必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      expectedLife: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback();
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('预期使用寿命必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      remark: {
        required: true,
        validator: (rule, value, callback) => {
          if (value && value.length > 100) {
            callback(new Error('备注不能超过100个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      },
      purchaseDate: {
        required: true,
        message: '请选择采购日期',
        trigger: 'blur'
      },
      purchaseAmount: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback();
          } else {
            const reg = /^[0-9]+(\.[0-9]{1,2})?$/; // 正数且最多两位小数
            if (!reg.test(value)) {
              callback(new Error('采购金额必须是正数，最多两位小数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      purchaseDocumentNo: {
        required: true,
        message: '请输入采购单据编号',
        trigger: 'blur'
      },
      contractNo: {
        required: true,
        message: '请输入合同编号',
        trigger: 'blur'
      },
      purchasePerson: {
        required: true,
        message: '请输入采购负责人',
        trigger: 'blur'
      },
      deliveryDate: {
        required: true,
        message: '请选择交付日期',
        trigger: 'blur'
      },
      acceptanceDate: {
        required: true,
        message: '请选择验收日期',
        trigger: 'blur'
      },
      acceptanceResult: {
        required: true,
        message: '请选择验收结果',
        trigger: 'change'
      },
      acceptanceNotes: {
        required: true,
        validator: (rule, value, callback) => {
          if (value && value.length > 100) {
            callback(new Error('验收备注不能超过100个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      },
      cancelReason: {
        required: true,
        validator: (rule, value, callback) => {
          if (value && value.length > 100) {
            callback(new Error('取消采购单原因不能超过100个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      },
      refuseReason: {
        required: true,
        validator: (rule, value, callback) => {
          if (value && value.length > 100) {
            callback(new Error('审核拒绝原因不能超过100个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      },
      promptMessage: {
        required: true,
        validator: (rule, value, callback) => {
          if (value && value.length > 100) {
            callback(new Error('催促通知不能超过100个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      },
      createTime: {
        required: true,
        message: '请选择创建时间',
        trigger: 'blur'
      },
      createBy: {
        required: true,
        message: '请输入创建人',
        trigger: 'blur'
      },
      updateTime: {
        required: true,
        message: '请选择更新时间',
        trigger: 'blur'
      },
      updateBy: {
        required: true,
        message: '请输入更新人',
        trigger: 'blur'
      }
    };
  }
  get isDisabled() {
    return (fieldName: string) => {
      // 只对初始非空字段禁用
      return this.initiallyNonEmptyFields.includes(fieldName);
    };
  }

  created() {
    // 获取路由参数
    const purchaseId = this.$route.query.purchaseId;
    const status = this.$route.query.status;

    // 确保 purchaseId 是字符串
    if (Array.isArray(purchaseId)) {
      this.purchaseId = purchaseId[0]; // 如果是数组，取第一个元素
    } else {
      this.purchaseId = purchaseId || ''; // 如果是字符串或 undefined，直接赋值
    }

    // 确保 status 是字符串
    if (Array.isArray(status)) {
      this.status = status[0] || ''; // 如果是数组，取第一个元素
    } else {
      this.status = status || ''; // 如果是字符串或 undefined，直接赋值
    }
    console.log(this.purchaseId, this.status);
    this.init();
  }

  private async init() {
    console.log(this.purchaseId);
    queryPurchaseDetailById({ purchaseId: this.purchaseId }).then((response) => {
      console.log("你好");
      // 将获取到的数据赋值给 ruleForm
      this.ruleForm = response.data.data;
      this.initiallyNonEmptyFields = Object.keys(this.ruleForm).filter(
        key => this.ruleForm[key] !== null &&
          this.ruleForm[key] !== undefined &&
          this.ruleForm[key] !== ''
      );
      console.log(this.ruleForm);
    }).catch((error) => {
      console.error('获取采购信息失败', error);
    });
  }
// 图片上传成功后的回调
  private handleImageSuccess(response: any, file: any) {
    if (response && response.data && response.code === 1) {
      this.ruleForm.image = response.data; // 将返回的图片URL赋值给ruleForm.image
      this.$message.success('图片上传成功');
    } else {
      this.$message.error('图片上传失败，请稍后重试');
    }
  }

  // 上传前的校验
  private beforeImageUpload(file: File) {
    const isJPGorPNG = file.type === 'image/jpeg' || file.type === 'image/png';
    const isLt2M = file.size / 1024 / 1024 < 2;

    if (!isJPGorPNG) {
      this.$message.error('上传图片只能是 JPG 或 PNG 格式!');
    }
    if (!isLt2M) {
      this.$message.error('上传图片大小不能超过 2MB!');
    }
    return isJPGorPNG && isLt2M;
  }
  // 重置图片，允许重新上传
  private resetImage() {
    this.ruleForm.image = '';
  }
  // 删除套餐菜品
  delDishHandle(index: any) {

  }

  public confirmPurchasingForm(formName: any) {
    ; (this.$refs[formName] as any).validate((valid: any) => {
      if (valid) {

        confirmPurchasingForm({
          purchaseId: this.ruleForm.purchaseId,
          status: 4,
          manufacturer: this.ruleForm.manufacturer,
          supplier: this.ruleForm.supplier,
          purchaseAmount: this.ruleForm.purchaseAmount,
          contractNo: this.ruleForm.contractNo,
          image: this.ruleForm.image,
          deliveryDate: this.ruleForm.deliveryDate,
          acceptanceDate: this.ruleForm.acceptanceDate,
          acceptanceResult: this.ruleForm.acceptanceResult,
          acceptanceNotes: this.ruleForm.acceptanceNotes,
          remark: this.ruleForm.remark
        })
          .then(res => {
            if (res && res.data && res.data.code === 1) {
              this.$message.success('操作成功！')
              this.$router.back();

                ; (this as any).$refs.ruleForm.resetFields()
              this.ruleForm = {
                purchaseId: '', // 采购ID
                equipmentType: '', // 设备类型
                equipmentName: '', // 设备名称
                modelNumber: '', // 型号
                specification: '', // 规格参数
                manufacturer: '', // 生产厂家
                supplier: '', // 供应商
                purchaseDate: '', // 采购日期
                purchaseAmount: '', // 采购金额
                quantity: '', // 采购数量
                unit: '', // 单位
                warrantyPeriod: '', // 保修期(月)
                expectedLife: '', // 预期使用寿命(月)
                purchaseDocumentNo: '', // 采购单据编号
                contractNo: '', // 合同编号
                purchasePerson: '', // 采购负责人
                image: '', // 采购设备图像
                deliveryDate: '', // 交付日期
                acceptanceDate: '', // 验收日期
                acceptanceResult: '', // 验收结果
                acceptanceNotes: '', // 验收备注
                remark: '', // 备注
                status: -2,
                cancelReason: '', // 取消采购单原因
                refuseReason: '', // 审核拒绝原因
                promptMessage: '', // 催促通知
                createTime: '', // 创建时间
                createBy: '', // 创建人
                updateTime: '', // 更新时间
                updateBy: '', // 更新人
              } as any
              this.status = ''
              this.purchaseId = ''
            } else {
              this.$message.error(res.data.msg)
            }
          })
          .catch(err => {
            this.$message.error('请求出错了：' + err.message)
          })
      } else {

        // 如果所有手动校验都通过，但仍然 valid 为 false，可能是其他字段的问题

        return this.$message.error('表单校验失败，请检查输入内容');
      }
    })

  }



  //提交审核采购单
  public submitForm(formName: any) {
    ; (this.$refs[formName] as any).validate((valid: any) => {
      if (valid) {
        this.ruleForm.status = 2;
        this.ruleForm.purchaseId = this.purchaseId;
        let params = { ...this.ruleForm } as any

        submitPurchasingForm( params )
          .then(res => {
            if (res && res.data && res.data.code === 1) {
              this.$message.success('操作成功！')

                ; (this as any).$refs.ruleForm.resetFields()
              this.ruleForm = {
                purchaseId: '', // 采购ID
                equipmentType: '', // 设备类型
                equipmentName: '', // 设备名称
                modelNumber: '', // 型号
                specification: '', // 规格参数
                manufacturer: '', // 生产厂家
                supplier: '', // 供应商
                purchaseDate: '', // 采购日期
                purchaseAmount: '', // 采购金额
                quantity: '', // 采购数量
                unit: '', // 单位
                warrantyPeriod: '', // 保修期(月)
                expectedLife: '', // 预期使用寿命(月)
                purchaseDocumentNo: '', // 采购单据编号
                contractNo: '', // 合同编号
                purchasePerson: '', // 采购负责人
                image: '', // 采购设备图像
                deliveryDate: '', // 交付日期
                acceptanceDate: '', // 验收日期
                acceptanceResult: '', // 验收结果
                acceptanceNotes: '', // 验收备注
                remark: '', // 备注
                status: -2,
                cancelReason: '', // 取消采购单原因
                refuseReason: '', // 审核拒绝原因
                promptMessage: '', // 催促通知
                createTime: '', // 创建时间
                createBy: '', // 创建人
                updateTime: '', // 更新时间
                updateBy: '', // 更新人
              } as any
              this.status = ''
              this.purchaseId = ''
              this.$router.back();
            } else {
              this.$message.error(res.data.msg)
            }
          })
          .catch(err => {
            this.$message.error('请求出错了：' + err.message)
          })
      } else {

        // 如果所有手动校验都通过，但仍然 valid 为 false，可能是其他字段的问题

        return this.$message.error('表单校验失败，请检查输入内容');
      }
    })
  }
}
