import  request  from '@/utils/request';

// 获取保养记录分页列表
export const getMaintenanceTaskPage = (params: any) => {
  return request({
    url: '/team_leader/pending_maintenance_verify',
    method: 'get',
    params
  });
};

// 根据ID查询保养任务详情
export const queryMaintenanceTaskById = (maintenanceId: string) => {
  return request({
    url: `/maintenance/maintenance_task/${maintenanceId}`,
    method: 'get'
  });
};

// 根据保养编号查询保养详情
export const queryMaintenanceDetailById = (maintenanceId: string) =>
  request({
    url: `/it_manager/maintenance_detail/${maintenanceId}`,
    method: 'get'
  });



// 获取各状态保养数量
export const getMaintenanceStatusListBy = (params: any) =>
  request({
    url: '/fault/statusCount',
    method: 'get',
    params
  });



// 班组长审核
export const auditMaintenance = (params: any) =>
  request({
    url: '/team_leader/verify_maintenance',
    method: 'post',
    data: params
  });

  
// 工区长审核
export const auditMaintenance2 = (params: any) =>
  request({
    url: '/area_leader/verify_maintenance',
    method: 'post',
    data: params
  });


  
// 获取保养验收列表
export const getMaintenanceIdVerifyList = (params: any) =>
  request({
    url: '/it_manager/pending_maintenance_sign',
    method: 'get',
    params
  })

// 提交电子签名
export const submitMaintenanceVerify = (data: any) =>
  request({
    url: '/it_manager/sign_maintenance',
    method: 'post',
    data
  })



  // 保养验收审核详情
  export function queryMaintenanceVerifyDetailById(maintenanceId: string) {
    return request({
      url: `/it_manager/maintenance_verify/${maintenanceId}`,
      method: 'get',
      params: { maintenanceId }
    })
  }
