import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import ImageUpload from '@/components/ImgUpload/index.vue'
import { submitPurchase } from '@/api/purchase'

@Component({
  name: 'addShop',
  components: {
    HeadLable,
    ImageUpload
  }
})
export default class extends Vue {
  private value: string = ''
  private actionType: string = ''
  private ruleForm = {
    equipmentName: '',
    equipmentType: '',
    modelNumber: '',
    specification: '',
    manufacturer: '',
    supplier: '',
    quantity: '',
    unit: '',
    warrantyPeriod: '',
    expectedLife: '',
    remark: '',
  }
  get rules() {
    return {
      equipmentName: {
        required: true,
        validator: (rule: any, value: string, callback: Function) => {
          if (!value) {
            callback(new Error('请输入名称'));
          } else {
            const reg = /^([A-Za-z0-9\u4e00-\u9fa5]){2,20}$/;
            if (!reg.test(value)) {
              callback(new Error('名称输入不符，请输入2-20个字符'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      equipmentType: {
        required: true,
        message: '请选择类型',
        trigger: 'change'
      },
      modelNumber: {
        required: false,
        message: '请输入型号',
        trigger: 'blur'
      },
      specification: {
        required: false,
        message: '请输入规格',
        trigger: 'blur'
      },
      manufacturer: {
        required: false,
        message: '请输入制造商',
        trigger: 'blur'
      },
      supplier: {
        required: false,
        message: '请输入供应商',
        trigger: 'blur'
      },
      quantity: {
        required: true,
        validator: (rule: any, value: string, callback: Function) => {
          if (!value) {
            callback(new Error('请输入数量'));
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('数量必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      unit: {
        required: false,
        message: '请输入单位',
        trigger: 'blur'
      },
      warrantyPeriod: {
        required: false,
        validator: (rule: any, value: string, callback: Function) => {
          // 如果 value 为空，则直接通过校验
          if (!value) {
            callback();
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('保修期必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },

      expectedLife: {
        required: false,
        validator: (rule: any, value: string, callback: Function) => {
          // 如果 value 为空，则直接通过校验
          if (!value) {
            callback();
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('预期寿命必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
