import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import InputAutoComplete from '@/components/InputAutoComplete/index.vue'
import TabChange from './tabChange.vue'
import Empty from '@/components/Empty/index.vue'
import {
  getMaintenanceTaskPage,
  queryMaintenanceDetailById,
  getMaintenanceStatusListBy,
  auditMaintenance2,
  queryMaintenanceTaskById
} from '@/api/maintenance'
import { status } from 'nprogress'
import { verify } from 'crypto'

@Component({
  components: {
    HeadLable,
    InputAutoComplete,
    TabChange,
    Empty,
  },
})
export default class extends Vue {
  private defaultActivity: any = 0
  private orderStatics = {
    all: 15,
    pending: 3,
    processing: 4,
    waitingClear: 5,
    cleared: 2,
    closed: 1
  }
  private row = {}
  private itemResult = ''
  private maintenanceCode = '' // 保养编号
  private dialogmaintenanceStatus = -1
  private clearDialogVisible = false
  private auditDialogVisible = false // 审核弹窗
  private refuseDialogVisible = false // 审核不通过原因弹窗
  private taskDialogVisible = false // 任务信息弹窗
  private valueTime = []
  private dialogVisible = false // 详情弹窗
  private remark = '' // 备注
  private refuseReason = '' // 审核不通过原因
  private deviceName = ''
  private counts: number = 15
  private page: number = 1
  private pageSize: number = 10
  private tableData = []
  private status = 0

  private diaForm: any = {} // 修改为对象
  private taskForm: any = {} // 修改为对象
  private isSearch: boolean = false
  private maintenanceStatus = 0 // 列表字段展示所需状态,用于分页请求数据

  // 审核不通过原因列表
  private refuseFaultList = [
    {
      label: '处理不完善',
      value: '处理不完善',
    },
    {
      label: '信息不完整',
      value: '信息不完整',
    },
    {
      label: '维修措施不当',
      value: '维修措施不当',
    },
    {
      label: '照片不清晰',
      value: '照片不清晰',
    },
    {
      label: '自定义原因',
      value: '自定义原因',
    },
  ]

  // 保养结果列表
  private itemResultList = [
    {
      label: '正常',
      value: '正常',
    },
    {
      label: '异常',
      value: '异常',
    },
  ]



  
  // 任务状态列表
  private taskStatusList = [
    {
      label: '执行中',
      value: 1,
    },
    {
      label: '待验收',
      value: 2,
    },
    {
      label: '已验收',
      value: 3,
    },
    {
      label: '已销号',
      value: 4,
    },
    {
      label: '被驳回',
      value: 5,
    }
  ]

  private maintenanceStatusList = [
    {
      label: '待验收',
      value: 2,
    },
    {
      label: '验收完成',
      value: 3,
    },
    
  ]
  
  // 工区列表（示例数据）
  private areaList = [
    { id: 1, name: '工区A' },
    { id: 2, name: '工区B' },
    { id: 3, name: '工区C' }
  ]
  
  // 班组列表（示例数据）
  private teamList = [
    { id: 1, name: '班组A' },
    { id: 2, name: '班组B' },
    { id: 3, name: '班组C' }
  ]

  created() {
    this.init(Number(this.$route.query.status) || 0)
  }

  mounted() {
    if (
      this.$route.query.maintenanceId &&
      this.$route.query.maintenanceId !== 'undefined'
    ) {
      this.goDetail(this.$route.query.maintenanceId, 2)
    }
    if (this.$route.query.status) {
      this.defaultActivity = Number(this.$route.query.status)
    }
  }
  
  // 获取工区名称
  private getAreaName(areaId: number): string {
    const area = this.areaList.find(item => item.id === areaId)
    return area ? area.name : '未知工区'
  }
  
  // 获取班组名称
  private getTeamName(teamId: number): string {
    const team = this.teamList.find(item => item.id === teamId)
    return team ? team.name : '未知班组'
  }
  

  // 获取保养结果
  private getitemResult(row: any): string {
    const type = this.itemResultList.find(item => item.value === row.itemResult)
    return type ? type.label : '未知类型'
  }
  

  // 获取保养状态
  private getmaintenanceStatus(row: any): string {
    const status = this.maintenanceStatusList.find(item => item.value === row.status)
    return status ? status.label : '未知状态'
  }
  
  // 获取任务状态文本
  private getTaskStatusText(status: number): string {
    const statusItem = this.taskStatusList.find(item => item.value === status)
    return statusItem ? statusItem.label : '未知状态'
  }
  
  // 初始化数据
  private init(activeIndex: number, isSearch: boolean = false) {
    console.log('初始化数据当前activeIndex:', activeIndex)
    this.maintenanceStatus = activeIndex
    this.isSearch = isSearch
    const params = {
      auditStatus: activeIndex,
      page: this.page,
      pageSize: this.pageSize,
      deviceName: this.deviceName,
      startTime: this.valueTime && this.valueTime.length ? this.valueTime[0] : null,
      completeTime: this.valueTime && this.valueTime.length ? this.valueTime[1] : null,
    }
    
    getMaintenanceTaskPage(params).then(res => {
      if (res.data.code === 1) {

        this.tableData = res.data.data.records || 0
        this.counts = res.data.total
      } else {
        this.$message.error(res.data.msg || '获取数据失败')
      }
    }).catch(() => {
      // 模拟数据，实际开发中可删除
      this.$message.error('未知错误请联系管理员')
    })
  }
  
  // 初始化函数（用于回车搜索）
  private initFun(status: number) {
    this.init(status, true)
  }
  
  //管理标签页点击事件
  change(activeIndex) {
    if (activeIndex === this.maintenanceStatus) return
    this.init(activeIndex)
    this.valueTime = []
    //this.dialogPurchaseStatus = 0
    this.$router.push('/sectionLeader/deviceReview/maintenanceAcceptance')
    console.log(activeIndex, '接收到了子组件的index')
  }

  
  // 处理页码变化
  private handleCurrentChange(val: number) {
    this.page = val
    this.init(this.maintenanceStatus)
  }
  
  // 处理每页条数变化
  private handleSizeChange(val: number) {
    this.pageSize = val
    this.page = 1
    this.init(this.maintenanceStatus)
  }
  
  // 关闭弹窗
  private handleClose() {
    this.dialogVisible = false
  }
  

  
  // 查看详情
  private goDetail(maintenanceId: string, status: number, row: any = {}) {
    this.row = row
    this.dialogmaintenanceStatus = status
    this.dialogVisible = true
    
    // 获取保养详情
    queryMaintenanceDetailById(maintenanceId).then(res => {
      if (res.data.code === 1) {
        this.diaForm = res.data.data
      } else {
        this.$message.error(res.data.msg || '获取详情失败')
      }
    }).catch(() => {
      // 模拟数据，实际开发中可删除
      this.$message.error('未知错误请联系管理员')
    })
  }
  
  // 审核保养
  private auditMaintenance2(row: any) {
    this.row = row
    this.dialogmaintenanceStatus = row.status
    this.auditDialogVisible = true
    // 获取保养详情
