  private confirmRefuse() {
    if (!this.refuseReason) {
      this.$message.error('请选择审核不通过原因')
      return
    }
    
   
    const params = {
      approved: false,
      maintenanceId: this.row.maintenanceId,
      satisfactionScore: null,
      verifyId: 1,
      verifyOpinion: this.remark == '' ? this.refuseReason : this.remark,
    };
    
    auditMaintenance2(params).then(res => {
      if (res.data.code === 1) {
        this.$message.success('审核通过成功！')
        this.clearDialogVisible = false
        this.dialogVisible = false
        this.refuseDialogVisible = false
        this.auditDialogVisible = false
        this.remark = ''
