        this.init(this.maintenanceStatus)
      } else {
        this.$message.error(res.data.msg || '操作失败')
      }
    }).catch(() => {
      this.$message.error('服务器繁忙')
      this.clearDialogVisible = false
      this.dialogVisible = false
      this.remark = ''
      this.init(this.maintenanceStatus)
    })
  }
  

  
