import  request  from '@/utils/request';

// 获取巡检记录分页列表
export const getInspectionTaskPage = (params: any) => {
  return request({
    url: '/team_leader/pending_inspection_verify',
    method: 'get',
    params
  });
};

// 根据ID查询巡检任务详情
export const queryInspectionTaskById = (inspectionId: string) => {
  return request({
    url: `/inspection/inspection_task/${inspectionId}`,
    method: 'get'
  });
};

// 根据巡检编号查询巡检详情
export const queryInspectionDetailById = (inspectionId: string) =>
  request({
    url: `/it_manager/inspection_detail/${inspectionId}`,
    method: 'get'
  });



// 获取各状态巡检数量
export const getInspectionStatusListBy = (params: any) =>
  request({
    url: '/fault/statusCount',
    method: 'get',
    params
  });



// 班组长审核
export const auditInspection = (params: any) =>
  request({
    url: '/team_leader/verify_inspection',
    method: 'post',
    data: params
  });
  // 工区长审核
export const auditInspection2 = (params: any) =>
  request({
    url: '/area_leader/verify_inspection',
    method: 'post',
    data: params
  });


  

// 获取巡检验收列表
export const getInspectionVerifyList = (params: any) =>
  request({
    url: '/it_manager/pending_inspection_sign',
    method: 'get',
    params
  })

// 提交电子签名
export const submitInspectionVerify = (data: any) =>
  request({
    url: '/it_manager/sign_inspection',
    method: 'post',
    data
  })


  // 巡检验收审核详情
  export function queryInspectionVerifyDetailById(inspectionId: string) {
    return request({
      url: `/it_manager/inspection_verify/${inspectionId}`,
      method: 'get',
      params: { inspectionId }
    })
  }
