import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import {
  getDevicePage,
  InboundById,
  batchInbound 
} from '@/api/warehouse.ts'
import InputAutoComplete from '@/components/InputAutoComplete/index.vue'
import Empty from '@/components/Empty/index.vue'

@Component({
  name: 'inbound',
  components: {
    HeadLable,
    InputAutoComplete,
    Empty
  }
})
export default class extends Vue {
  private equipmentName: any = ''
  private counts: number = 0
  private page: number = 1
  private pageSize: number = 10
  private tableData: any[] = []
  private selectedDevices: any[] = [] // 存储选中的设备
  private equipmentType = ''
  private status = 1
  private isSearch: boolean = false
  private dialogVisible = false; // 控制单个入库弹窗显示
  private batchDialogVisible = false; // 控制批量入库弹窗显示
  private currentDeviceId = '';  // 当前操作的设备ID
  
  // 单个入库表单
  private inboundForm = {
    areald: '',                  // 区域ID
    deviceId: '',               // 设备ID
    inboundBy: '',              // 入库操作人
    inspectionDate: '',         // 质检日期
    inspectionResult: '',       // 质检结果
    storageLocation: '',        // 货架位置
    warehouseId: ''             // 仓库ID
  };

  // 批量入库表单
  private batchInboundForm = {
    deviceIds: [] as string[],  // 设备ID数组
    inboundBy: '',              // 入库操作人
    inspectionDate: '',         // 质检日期
    inspectionResult: '',       // 质检结果
    storageLocation: '',        // 货架位置
    warehouseId: ''             // 仓库ID
  };

  created() {
    this.init()
  }

  initProp(val) {
    this.equipmentName = val
    this.initFun()
  }

  initFun() {
    this.page = 1
    this.init()
  }

  private async init(isSearch?) {
    this.isSearch = isSearch
    await getDevicePage({
      page: this.page,  
      pageSize: this.pageSize,
      equipmentName: this.equipmentName || undefined,
      equipmentType: this.equipmentType || undefined,
      status: 1
    })
      .then(res => {
        if (res.data.code === 1) {
          this.tableData = res.data && res.data.data && res.data.data.records
          this.counts = Number(res.data.data.total)
        }
      })
      .catch(err => {
        this.$message.error('请求出错了：' + err.message)
      })
  }

  // 表格选择项变化
  private handleSelectionChange(val: any[]) {
    this.selectedDevices = val;
  }

  // 显示批量入库对话框
  private showBatchInboundDialog() {
    if (this.selectedDevices.length === 0) {
      this.$message.warning('请至少选择一台设备');
      return;
    }
    this.batchInboundForm.deviceIds = this.selectedDevices.map(device => device.deviceId);
    this.batchDialogVisible = true;
  }

  // 单个设备入库
  private async deviceInbound(deviceId: string) {
    this.currentDeviceId = deviceId;
    this.inboundForm.deviceId = deviceId;
    this.dialogVisible = true;
  }

  // 提交单个入库
  private async submitInbound() {
    try {
      // 必填字段验证
      if (!this.inboundForm.inboundBy || !this.inboundForm.inspectionDate ||
        !this.inboundForm.inspectionResult || !this.inboundForm.storageLocation) {
        this.$message.warning('请填写完整入库信息');
        return;
      }

      const res = await InboundById({
        ...this.inboundForm,
        inspectionDate: this.inboundForm.inspectionDate + ' 00:00:00'
      });

      if (res.data.code === 1) {
        this.$message.success('入库成功');
        this.dialogVisible = false;
        this.init();
      }
    } catch (err) {
      this.$message.error('操作失败: ' + (err.message || '未知错误'));
    }
  }

  // 提交批量入库
  private async submitBatchInbound() {
    try {
      // 必填字段验证
      if (!this.batchInboundForm.inboundBy || !this.batchInboundForm.inspectionDate ||
        !this.batchInboundForm.inspectionResult || !this.batchInboundForm.storageLocation) {
        this.$message.warning('请填写完整入库信息');
        return;
      }

      const params = {
        ...this.batchInboundForm,
        deviceIds: this.selectedDevices.map(device => device.deviceId),
        inspectionDate: this.batchInboundForm.inspectionDate + ' 00:00:00'
      };

      const res = await batchInbound(params);

      if (res.data.code === 1) {
        this.$message.success(`成功入库 ${this.selectedDevices.length} 台设备`);
        this.batchDialogVisible = false;
        this.selectedDevices = [];
        this.init();
      }
    } catch (err) {
      this.$message.error('批量入库失败: ' + (err.message || '未知错误'));
    }
  }

  // 重置表单
  private resetForm() {
    this.inboundForm = {
      areald: '',
      deviceId: this.currentDeviceId,
      inboundBy: '',
      inspectionDate: '',
      inspectionResult: '',
      storageLocation: '',
      warehouseId: ''
    };
  }

  private handleSizeChange(val: any) {
    this.pageSize = val
    this.init()
  }

  private handleCurrentChange(val: any) {
    this.page = val
    this.init()
  }
}
