import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import ImageUpload from '@/components/ImgUpload/index.vue'
import { submitWarehouseRequest } from '@/api/warehouse'

@Component({
  name: 'addShop',
  components: {
    HeadLable,
    ImageUpload
  }
})
export default class extends Vue {
  private value: string = ''
  private actionType: string = ''
  private ruleForm = {
    requestCode: '',
    warehouseId: '',
    applicantName: '',
    applicantDepartment: '',
    applicantPhone: '',
    equipmentName: '',
    equipmentType: '',
    quantity: 1,
    targetAreaId: '',
    targetTeamId: '',
    targetLocation: '',
    plannedOutboundDate: '',
    remark: ''
  }
  private warehouseOptions = [
    { label: '仓库A', value: '1' },
    { label: '仓库B', value: '2' }
  ]

  get rules() {
    return {
      requestCode: {
        required: true,
        message: '请输入出库申请单号',
        trigger: 'blur'
      },
      warehouseId: {
        required: true,
        message: '请选择出库仓库',
        trigger: 'change'
      },
      applicantName: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback(new Error('请输入申请人姓名'));
          } else {
            const reg = /^[\u4e00-\u9fa5]{2,20}$/; // 中文姓名，2-20个字符
            if (!reg.test(value)) {
              callback(new Error('申请人姓名格式不符，请输入2-20个中文字符'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      applicantDepartment: {
        required: true,
        message: '请输入申请部门',
        trigger: 'blur'
      },
      applicantPhone: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback(new Error('请输入申请人电话'));
          } else {
            const reg = /^1[3-9]\d{9}$/; // 手机号码正则
            if (!reg.test(value)) {
              callback(new Error('请输入有效的手机号码'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      equipmentName: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback(new Error('请输入设备名称'));
          } else {
            const reg = /^([A-Za-z0-9\u4e00-\u9fa5]){2,100}$/; // 设备名称，2-100个字符
            if (!reg.test(value)) {
              callback(new Error('设备名称格式不符，请输入2-100个字符'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      equipmentType: {
        required: true,
        message: '请选择设备类型',
        trigger: 'change'
      },
      quantity: {
        required: true,
        validator: (rule, value, callback) => {
          if (!value) {
            callback(new Error('请输入出库数量'));
          } else {
            const reg = /^[1-9]\d*$/; // 正整数
            if (!reg.test(value)) {
              callback(new Error('出库数量必须是正整数'));
            } else {
              callback();
            }
          }
        },
        trigger: 'blur'
      },
      targetLocation: {
        required: true,
        validator: (rule, value, callback) => {
          if (value && value.length > 255) {
            callback(new Error('目标位置描述不能超过255个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      },
      remark: {
        required: false,
        validator: (rule, value, callback) => {
          if (value && value.length > 500) {
            callback(new Error('备注不能超过500个字符'));
          } else {
            callback();
          }
        },
        trigger: 'blur'
      }
    };
  }


  created() {
    // this.getDishTypeList()
    // this.actionType = this.$route.query.id ? 'edit' : 'add'
    // if (this.actionType == 'edit') {
    //   this.init()
    // }
    this.init()
  }

  private async init() {

  }
  private seachHandle() {

  }
  // 获取套餐分类
  private getDishTypeList() {

  }  // 删除套餐菜品
  delDishHandle(index: any) {

  }

  // 获取添加菜品数据 - 确定加菜倒序展示
  private getCheckList(value: any) {

  }

  public submitForm(formName: any, st: any) {
    ; (this.$refs[formName] as any).validate((valid: any) => {
      if (valid) {
        let prams = { ...this.ruleForm } as any
        submitWarehouseRequest(prams)
          .then(res => {
            if (res && res.data && res.data.code === 1) {
              this.$message.success('出库设备申请成功！')
              if (!st) {
                this.$router.push({ path: '/teamLeader/outbound' })
              } else {
                ; (this as any).$refs.ruleForm.resetFields()
                this.ruleForm = {
                  requestCode: '',
                  warehouseId: '',
                  applicantName: '',
                  applicantDepartment: '',
                  applicantPhone: '',
                  equipmentName: '',
                  equipmentType: '',
                  quantity: 1,
                  targetAreaId: '',
                  targetTeamId: '',
                  targetLocation: '',
                  plannedOutboundDate: '',
                  remark: ''
                } as any
              }
            } else {
              this.$message.error(res.data.msg)
            }
          })
          .catch(err => {
            this.$message.error('请求出错了：' + err.message)
          })
      } else {
        return this.$message.error('表单校验失败，请检查输入内容');
      }
    })
  }
}
