import { Component, Vue } from 'vue-property-decorator';
import Empty from '@/components/Empty/index.vue';
import { getDeviceList,queryDeviceById } from '@/api/device';

@Component({
  components: {
    Empty
  }
})
export default class DeviceInfo extends Vue {
  private deviceName = '';
  private deviceType = '';
  private status = '';
  private tableData: any[] = [];
  private counts = 0;
  private page = 1;
  private pageSize = 10;
  private isSearch = false;
  private detailDialog = false;
  private detailForm: any = {};

  created() {
    this.init();
  }

  private async init(isSearch?: boolean) {
    this.isSearch = isSearch === true;
    const params = {
      page: this.page,
      pageSize: this.pageSize,
      deviceName: this.deviceName || undefined,
      deviceType: this.deviceType || undefined,
      status: this.status || undefined
    };
    const res = await getDeviceList(params);
    if (res.data.code === 1) {
      this.tableData = res.data.data.records;
      this.counts = res.data.data.total;
    }
    
  }

  private initFun() {
    this.init(true);
  }

  private async getDetail(row: any) {
    try {
      const res = await queryDeviceById(row.deviceId);
      if (res.data.code === 1) {
        this.detailForm = res.data.data;
      } else {
        this.$message.error(res.data.msg || '获取设备详情失败');
      }
      
      // 显示详情对话框
      this.detailDialog = true;
    } catch (error) {
      console.error('获取设备详情失败:', error);
      this.$message.error('获取设备详情失败，请重试');
      // 出错时仍然显示基本信息
      this.detailForm = { ...row };
      this.detailDialog = true;
    }
  }

  private viewDeviceRecord(deviceId: number) {
    // 跳转到设备履历页面
    this.$router.push({
      path: '/director/device/record',
      query: { deviceId: deviceId.toString() }
    });
  }

  private handleClose() {
    this.detailDialog = false;
  }

  private getStatusText(status: number) {
    const statusMap: { [key: number]: string } = {
      0: '报废',
      1: '正常',
      2: '维修中',
      3: '停用',
      4: '仓库存放'
    };
    return statusMap[status] || '未知';
  }

  private getMaintenanceTypeText(type: number) {
    const typeMap: { [key: number]: string } = {
      1: '自维',
      2: '托管',
      3: '技服'
    };
    return typeMap[type] || '未知';
  }

  private handleSizeChange(val: number) {
    this.pageSize = val;
    this.init();
  }

  private handleCurrentChange(val: number) {
    this.page = val;
    this.init();
  }
}
