import { Component, Vue } from 'vue-property-decorator';
import HeadLable from '@/components/HeadLable/index.vue';
import {
  getWarehouseDetailPage,
  outbound
} from '@/api/warehouse.ts';
import InputAutoComplete from '@/components/InputAutoComplete/index.vue';
import Empty from '@/components/Empty/index.vue';

@Component({
  name: 'outbound',
  components: {
    HeadLable,
    InputAutoComplete,
    Empty
  }
})
export default class extends Vue {
  private equipmentName: any = '';
  private requestCode: any = '';
  private counts: number = 0;
  private page: number = 1;
  private pageSize: number = 10;
  private tableData: any[] = [];
  private plannedOutboundDate = [];
  private equipmentType = '';
  private selectedEquipmentName = '';
  private selectedQuantity = 0;
  private confirmDialogVisible = false;
  private isSearch = false;

  // 获取设备类型
  getEquimentType(row: any) {
    if (row.equipmentType === 1) {
      return '客服设备';
    } else if (row.equipmentType === 2) {
      return '机电设备';
    } else if (row.equipmentType === 3) {
      return '消防设备';
    } else if (row.equipmentType === 4) {
      return '电梯设备';
    } else {
      return '状态错误';
    }
  }

  created() {
    this.init();
  }

  initProp(val) {
    this.equipmentName = val;
    this.initFun();
  }

  initFun() {
    this.page = 1;
    this.init();
  }

  // 点击出库按钮，弹出确认对话框
  confirmOutbound(row: any) {
    this.requestCode = row.requestCode;
    this.confirmDialogVisible = true;
    this.equipmentName = row.equipmentName;
    this.selectedQuantity = row.quantity;
    this.selectedEquipmentName = row.equipmentName;
  }

  // 确认出库操作
  confirmOutboundAction() {
    outbound({
      requestCode: this.requestCode,
      equipmentName:this.equipmentName
    })
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success("出库成功！");
          this.requestCode = '';
          this.confirmDialogVisible = false;
          this.init();
        } else {
          this.$message.error(res.data.msg);
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message);
      });
  }

  // 关闭对话框
  handleClose() {
    this.confirmDialogVisible = false;
  }

  private async init(isSearch?) {
    await getWarehouseDetailPage({
      page: this.page,
      pageSize: this.pageSize,
      name: this.equipmentName || undefined,
      status: 1,
      equipmentType: this.equipmentType || undefined,
      beginTime:
        this.plannedOutboundDate && this.plannedOutboundDate.length > 0
          ? this.plannedOutboundDate[0]
          : undefined,
      endTime:
        this.plannedOutboundDate && this.plannedOutboundDate.length > 0
          ? this.plannedOutboundDate[1]
          : undefined,
    })
      .then(res => {
        if (res.data.code === 1) {
          this.tableData = res.data && res.data.data && res.data.data.records;
          this.counts = Number(res.data.data.total);
        }
      })
      .catch(err => {
        this.$message.error('请求出错了：' + err.message);
      });
  }

  private handleSizeChange(val: any) {
    this.pageSize = val;
    this.init();
  }

  private handleCurrentChange(val: any) {
    this.page = val;
    this.init();
  }
}
