import { Component, Vue } from 'vue-property-decorator'
import TabChange from './tabChange.vue'
import Empty from '@/components/Empty/index.vue'
import vueEsign from 'vue-esign' 
import {
  getMaintenanceIdVerifyList,
  submitMaintenanceVerify,
  queryMaintenanceDetailById,
  queryMaintenanceVerifyDetailById
} from '@/api/maintenance'

@Component({
  components: {
    TabChange,
    Empty,
    vueEsign,
  },
})
export default class extends Vue {
  private defaultActivity: any = 0
  private orderStatics = {
    all: 15,
    pending: 3,
    processing: 4,
    completed: 2,
    closed: 1
  }
  private valueTime = []
  private dialogVisible = false
  private deviceName = ''
  private counts: number = 0
  private page: number = 1
  private pageSize: number = 10
  private tableData = []
  private diaForm: any = {}
  private isSearch: boolean = false
  private maintenanceStatus = 0

  private signDialogVisible = false
  private signatureImg = ''
  private satisfactionScore = 3
  private currentMaintenanceId = ''

    //审核详情弹窗
    private auditDialogVisible = false
  private auditForm: any = {}

  // // 检查结果列表
  // private itemResultList = [
  //   {
  //     label: '正常',
  //     value: 1,
  //   },
  //   {
  //     label: '异常',
  //     value: 2,
  //   }
  // ]

  // 巡检状态列表
  private maintenanceStatusList = [
  {
        label: '全部审核单',
        value: 0
      },
      {
        label: '待班组长审核',
        value: 1
      },
      {
        label: '待工区长审核',
        value: 2
      },
      {
        label: '待主任签字',
        value: 3
      },
      {
        label: '已完成',
        value: 4
      }
  ]
  
  // 工区列表（示例数据）
  private areaList = [
    { id: 1, name: '工区A' },
    { id: 2, name: '工区B' },
    { id: 3, name: '工区C' }
  ]
  
  // 班组列表（示例数据）
  private teamList = [
    { id: 1, name: '班组A' },
    { id: 2, name: '班组B' },
    { id: 3, name: '班组C' }
  ]

  created() {
    this.init(Number(this.$route.query.status) || 0)
  }

  mounted() {
    if (this.$route.query.maintenanceId) {
      this.goDetail(this.$route.query.maintenanceId, 2)
    }
    if (this.$route.query.status) {
      this.defaultActivity = Number(this.$route.query.status)
    }
  }
  
  // 获取工区名称
  private getAreaName(areaId: number): string {
    const area = this.areaList.find(item => item.id === areaId)
    return area ? area.name : '未知工区'
  }
  
  // 获取班组名称
  private getTeamName(teamId: number): string {
    const team = this.teamList.find(item => item.id === teamId)
    return team ? team.name : '未知班组'
  }
  
  // // 获取检查结果文本
  // private getItemResultText(result: number): string {
  //   const item = this.itemResultList.find(item => item.value === result)
  //   return item ? item.label : '未知结果'
  // }
  
  // // 获取检查结果
  // private getItemResult(row: any): string {
  //   return this.getItemResultText(row.itemResult)
  // }
  
  // 获取巡检状态文本
  private getMaintenanceStatusText(status: number): string {
    const item = this.maintenanceStatusList.find(item => item.value === status)
    return item ? item.label : '未知状态'
  }
  
  // 获取巡检状态
  private getMaintenanceStatus(row: any): string {
    return this.getMaintenanceStatusText(row.status)
  }

  private formatDateToDay(dateTime: string): string {
    if (!dateTime) return '';
    return dateTime.split(' ')[0];
  }
  
  // 初始化数据
  private init(status: number, isSearch: boolean = false) {
    this.maintenanceStatus = status
    this.isSearch = isSearch
    const params = {
      page: this.page,
      pageSize: this.pageSize,
      deviceName: this.deviceName,
      startTime: this.valueTime && this.valueTime.length ? this.valueTime[0] : null,
      completeTime: this.valueTime && this.valueTime.length ? this.valueTime[1] : null,
      auditStatus: this.maintenanceStatus === 0 ? undefined : status,
    }
    
    getMaintenanceIdVerifyList(params).then(res => {
      if (res.data.code === 1) {
        this.tableData = res.data.data.records || []
        this.counts = res.data.data.total || 0
      } else {
        this.$message.error(res.data.msg || '获取数据失败')
      }
    }).catch(() => {
      this.$message.error('未知错误请联系管理员')
    })
  }
  
  // 初始化函数（用于回车搜索）
  private initFun(status: number) {
    this.init(status, true)
  }
  
  // 管理标签页点击事件
  private change(activeIndex: number) {
    if (activeIndex === this.maintenanceStatus) return
    this.init(activeIndex)
    this.valueTime = []
    this.$router.push('/director/deviceReview/maintenanceAcceptance')
  }

  // 处理页码变化
  private handleCurrentChange(val: number) {
    this.page = val
    this.init(this.maintenanceStatus)
  }
  
  // 处理每页条数变化
  private handleSizeChange(val: number) {
    this.pageSize = val
    this.page = 1
    this.init(this.maintenanceStatus)
  }
  
  // 关闭弹窗
  private handleClose() {
    this.dialogVisible = false
  }
  
  // 查看详情
  private goDetail(maintenanceId: string, status: number, row: any = {}) {
    this.dialogVisible = true
    
    queryMaintenanceDetailById(maintenanceId).then(res => {
      if (res.data.code === 1) {
        this.diaForm = res.data.data
      } else {
        this.$message.error(res.data.msg || '获取详情失败')
      }
    }).catch(() => {
      this.$message.error('未知错误请联系管理员')
    })
  }

  private handleSign(maintenanceId: string) {
  this.currentMaintenanceId = maintenanceId
  this.signDialogVisible = true
  // 每次打开时重置签名状态
  this.$nextTick(() => {
    this.handleReset() // 确保每次打开都清空签名
    this.satisfactionScore = 3 // 重置评分
  })
}

// Add these methods:
// 重设签名
private handleReset() {
  const esign = this.$refs.esign as any
  if (esign) {
    esign.reset() // 清除画布
    this.signatureImg = '' // 清空预览
  }
}

// 生成签名
private handleGenerate() {
  (this.$refs.esign as any).generate().then((res: string) => {
    this.signatureImg = res
  }).catch(() => {
    this.$message.error('请先签名')
  })
}

// 提交签名和满意度
private async submitSign() {
  if (!this.signatureImg) {
    this.$message.warning('请先完成签名')
    return
  }

  try {
    const params = {
      maintenanceId: this.currentMaintenanceId,
      satisfactionScore: this.satisfactionScore,
      signature: this.signatureImg
    }


    const res = await submitMaintenanceVerify(params)
    if (res.data.code === 1) {
      this.$message.success('签字提交成功')
      this.signDialogVisible = false
      this.init(this.maintenanceStatus) // 刷新列表
     }
    
    console.log('提交的签字数据:', JSON.stringify(params))
  } catch (error) {
    this.$message.error('提交失败: ' + error.message)
  }
}

// 打开审核详情弹窗
private showAuditDetail(maintenanceId: string) {
    this.auditDialogVisible = true
    this.loadAuditData(maintenanceId)
  }

  // 加载审核数据
  private loadAuditData(maintenanceId: string) {
    queryMaintenanceVerifyDetailById(maintenanceId).then(res => {
      if (res.data.code === 1) {
        this.auditForm = res.data.data
      } else {
        this.$message.error(res.data.msg || '获取审核详情失败')
      }
    }).catch(err => {
      this.$message.error('获取审核详情出错: ' + err.message)
    })
  }

  // 获取审核步骤文本
  private getVerifyStepText(step: number): string {
    switch (step) {
      case 1: return '班组长审核'
      case 2: return '工区长审核'
      case 3: return '主管领导审核'
      case 4: return '已完成'
    }
  }

  // 获取步骤状态描述
  private getStepDescription(step: number): string {
    if (step < this.auditForm.verifyStep) {
      return '已完成 - ' + this.auditForm.verifyTime
    } else if (step === this.auditForm.verifyStep) {
      return '进行中'
    } else {
      return '待处理'
    }
  }

  // 获取当前激活的步骤
  private getActiveStep(step: number): number {
    return step || 0
  }

  // 获取状态样式类
  private getStatusClass(step: number): string {
    switch (step) {
      case 1: return 'status1'
      case 2: return 'status2'
      case 3: return 'status3'
      default: return 'status0'
    }
  }
}
