import { Component, Vue } from 'vue-property-decorator'
import HeadLable from '@/components/HeadLable/index.vue'
import InputAutoComplete from '@/components/InputAutoComplete/index.vue'
import TabChange from './tabChange.vue'
import Empty from '@/components/Empty/index.vue'
import {
  getWarehouseDetailPage,
  queryOutboundDetailById,
  getoutboundStatusListBy,
  cancelOutbound,
} from '@/api/warehouse'
import { status } from 'nprogress'

@Component({
  components: {
    HeadLable,
    InputAutoComplete,
    TabChange,
    Empty,
  },
})
export default class extends Vue {
  private defaultActivity: any = 0
  private orderStatics = {}
  private row = {}
  private equipmentType = ''
  private requestCode = '' //出库申请单号
  private dialogrequestStatus = -2
  private cancelDialogVisible = false
  private cancelReason = ''
  private valueTime = []
  private dialogVisible = false //详情弹窗
  private remark = '' //自定义原因
  private equipmentName = ''
  private status = -1
  private counts: number = 0
  private page: number = 1
  private pageSize: number = 10
  private tableData = []
  private canceloutboundList = [
    {
      label: '重复采购',
      value: '重复采购',
    },
    {
      label: '订单重复',
      value: '订单重复',
    },
    {
      label: '项目取消',
      value: '项目取消',
    },
    {
      label: '订单内容填写错误',
      value: '订单内容填写错误',
    },
    {
      label: '自定义原因',
      value: '自定义原因',
    },
  ]

  private diaForm = []
  private isSearch: boolean = false
  private outboundStatus = -1 //列表字段展示所需订单状态,用于分页请求数据
  private dialogOutboundStatus = 0 //弹窗所需订单状态，用于详情展示字段

  private outboundList = [
    {
      label: '全部申请',
      value: -1,
    },
    {
      label: '待审核',
      value: 0,
    },
    {
      label: '待出库',
      value: 1,
    },
    {
      label: '已完成',
      value: 2,
    },
    {
      label: '已驳回',
      value: 3,
    }
  ]

  created() {
    this.init(Number(this.$route.query.status) || -1)
  }

  mounted() {
    //如果有值说明是消息通知点击进来的
    /*     if (
          this.$route.query.orderId &&
          this.$route.query.orderId !== 'undefined'
        ) {
          this.goDetail(this.$route.query.orderId, 2)
        }
        if (this.$route.query.status) {
          this.defaultActivity = this.$route.query.status
        } */
    // console.log(this.$route.query, 'this.$route')
  }

  initFun(outboundStatus) {
    this.page = 1
    this.init(outboundStatus)
  }

  //管理标签页点击事件
  change(activeIndex) {
    if (activeIndex === this.outboundStatus) return
    this.init(activeIndex)
    this.valueTime = []
    this.dialogOutboundStatus = 0
    this.$router.push('/teamLeader/outbound')
    console.log(activeIndex, '接收到了子组件的index')
  }

  //获取待处理，待派送，派送中数量
  getOutBoundListBy4Status() {
    getoutboundStatusListBy({})
      .then((res) => {
        if (res.data.code === 1) {
          this.orderStatics = res.data.data
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }

  init(activeIndex: number = -1, isSearch?) {
    this.isSearch = isSearch
    const params = {
      page: this.page,
      pageSize: this.pageSize,
      equipmentName: this.equipmentName || undefined,
      equipmentType: this.equipmentType || undefined,
      beginTime:
        this.valueTime && this.valueTime.length > 0
          ? this.valueTime[0]
          : undefined,
      endTime:
        this.valueTime && this.valueTime.length > 0
          ? this.valueTime[1]
          : undefined,
      status: activeIndex === -1 ? undefined : activeIndex,
      // isDeleted:activeIndex === 5 ? 1 : 0
    }
    getWarehouseDetailPage({ ...params })
      .then((res) => {
        if (res.data.code === 1) {
          console.log(this.outboundStatus)
          this.tableData = res.data.data.records
          this.outboundStatus = activeIndex
          console.log(activeIndex)
          this.counts = Number(res.data.data.total)
          this.getOutBoundListBy4Status()
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }

  // 添加更改
  private addOutWarehouseRequest(st: any) {
    this.$router.push({ path: '/teamLeader/outbound/add' })

  }

  //获取采购状态
  getOutboundType(row: any) {
    if (row.status === 0) {
      return '待审核'
    } else if (row.status === 1) {
      return '待出库'
    } else if (row.status === 2) {
      return '已完成'
    } else if (row.status === 3) {
      return '已驳回'
    } else {
      return '状态错误'
    }
  }

  //获取设备类型
  getEquimentType(row: any) {
    if (row.equipmentType === 1) {
      return '客服设备'
    } else if (row.equipmentType === 2) {
      return '机电设备'
    } else if (row.equipmentType === 3) {
      return '消防设备'
    } else if (row.equipmentType === 4) {
      return '电梯设备'
    } else {
      return '状态错误'
    }
  }


  // 查看详情
  async goDetail(id: any, status: number, row?: any) {
    // console.log(111, index, row)
    this.diaForm = []
    this.dialogVisible = true
    this.dialogOutboundStatus = status
    this.requestCode = id
    console.log("查看详情Id" + id)
    const { data } = await queryOutboundDetailById({ requestCode: id })
    this.diaForm = data.data
    this.row = row || { id: this.$route.query.requestCode, status: status }
    /*     if (this.$route.query.requestCode) {
          this.$router.push('/purchase')
        } */
  }
  cancelOutbound(row: any) {
    this.cancelDialogVisible = true
    this.requestCode = row.requestCode
    this.dialogVisible = false
    this.remark = ''
    this.dialogrequestStatus = row.status
  }

  //确认取消或拒绝订单并填写原因
  confirmCancel(type) {
    if (!this.remark) {
      return this.$message.error(`请选择取消原因`)
    }

    ; cancelOutbound({
      requestCode: this.requestCode,
      status: 2,
      // eslint-disable-next-line standard/computed-property-even-spacing
      remark: this.cancelReason
    })
      .then((res) => {
        if (res.data.code === 1) {
          this.$message.success('操作成功')
          this.cancelDialogVisible = false
          this.requestCode = ''
          // this.dialogOrderStatus = 0
          this.init(this.status)
        } else {
          this.$message.error(res.data.msg)
        }
      })
      .catch((err) => {
        this.$message.error('请求出错了：' + err.message)
      })
  }
  handleClose() {
    this.dialogVisible = false
  }

  private handleSizeChange(val: any) {
    this.pageSize = val
    this.init(this.outboundStatus)
  }

  private handleCurrentChange(val: any) {
    this.page = val
    this.init(this.outboundStatus)
  }
}
