import request from '@/utils/request'

// 查询列表页接口
export const getOrderDetailPage = (params: any) => {
  return request({
    url: '/purchasing_offer/page',
    method: 'get',
    params
  })
}


// 新增采购任务
export const submitPurchase = (params: any) => {
  return request({
    url: '/section_chief/set_equipment_purchase',
    method: 'post',
    data: { ...params }
  })
}

//采购单取消
export const purchaseCancel = (params: any) => {
  return request({
    url: '/section_chief/cancel_purchase_form',
    method: 'put' /*  */,
    data: { ...params }
  })
}


//财务人员接收采购单
export const receivePurchase = (params: any) => {
  return request({
    url: '/purchasing_offer/receive_purchase_form',
    method: 'put' /*  */,
    data: { ...params }
  })
}

//财务人员完成采购
export const confirmPurchasingForm = (params: any) => {
  return request({
    url: '/purchasing_offer/confirm_purchasing_form',
    method: 'put' /*  */,
    data: { ...params }
  })
}


//财务人员提交采购单的审核
export const submitPurchasingForm = (params: any) => {
  return request({
    url: '/purchasing_offer/submit_purchasing_form',
    method: 'put' /*  */,
    data: { ...params }
  })
}




// 查看接口
export const queryPurchaseDetailById = (params: any) => {
  return request({ 
    url: `/purchasing_offer/PurchaseById/${params.purchaseId}`,
    method: 'get'
  })
}

//审核通过
export const auditPurchase = (params: any) => {
  return request({
    url: '/section_chief/reviewed_purchase_form',
    method: 'put' /*  */,
    data: { ...params }
  })
}

//获取各个状态数量
export const getPurchaseListBy = (params: any) => {
  return request({
    url: '/section_chief/statistics',
    method: 'get' /*  */
  })
}


// 导出已完成的采购单
export const exportCompletedPurchases = (params?: any) => {
  return request({
    url: '/purchasing_offer/export_completed',
    method: 'get',
    responseType: 'blob',
    params
  })
}
