import Vue from "vue";
import Router from "vue-router";
import Layout from "@/layout/Layout.vue";
import {
  getToken,
  setToken,
  removeToken,
  getStoreId,
  setStoreId,
  removeStoreId,
  setUserInfo,
  getUserInfo,
  removeUserInfo
} from "@/utils/cookies";
import store from "@/store";
import { RouterLinkStub } from "@vue/test-utils";

Vue.use(Router);

const router = new Router({
  scrollBehavior: (to, from, savedPosition) => {
    if (savedPosition) {
      return savedPosition;
    }
    return { x: 0, y: 0 };
  },
  base: process.env.BASE_URL,
  routes: [
    {
      path: "/",  
      component: () =>
        import(/* webpackChunkName: "login" */ "@/views/login/index.vue"),
      meta: { title: "设备管理系统", hidden: true, notNeedAuth: true }
    },
    {
      path: "/404",
      component: () => import(/* webpackChunkName: "404" */ "@/views/404.vue"),
      meta: { title: "设备管理系统", hidden: true, notNeedAuth: true }
    },
    //工区长
    {
      path: "/sectionLeader",
      component: Layout,
      redirect: "/sectionLeader/dashboard",
      meta: { title: "工区长", icon: "dashboard" },
      children: [
        {
          path: "dashboard",
          component: () =>
            import(/* webpackChunkName: "dashboard" */ "@/views/sectionLeader/dashboard/index.vue"),
          name: "sectionLeaderDashboard",
          meta: { title: '工作台', icon: 'dashboard', affix: true }
        },
        {
          path: "statistics",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/statistics/index.vue"),
          meta: {
            title: "数据统计",
            icon: "icon-statistics",
          }
        },
        {
          path: "purchase",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/purchase/index.vue"),
          meta: {
            title: "设备采购管理",
            icon: "icon-order",
          }
        },
        {
          path: "purchase/add",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/purchase/addPurchase.vue"),
          meta: {
            title: "添加采购任务",
            hidden: true,
          }
        },
        {
          path: "outbound",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/sectionLeader/outbound/index.vue"),
          meta: {
            title: "设备出库申请审核",
            icon: "icon-combo",
          }
        },
           {
          path: "deviceReview",
          component: () =>
            import(/* webpackChunkName: "deviceReview" */ "@/views/sectionLeader/deviceReview/index.vue"),
          redirect: "/deviceReview/faultClear",
          meta: {
            title: "设备审核",
            icon: "icon-dish",
          },
          // 添加子路由
          children: [
            {
              path: "faultClear",
              component: () =>
                import(/* webpackChunkName: "faultClear" */ "@/views/sectionLeader/deviceReview/faultClear/index.vue"),
              meta: {
                title: "故障销号",
                icon: "icon-dish",
              }
            },
            {
              path: '/sectionLeader/deviceReview/faultClear/face-recognition',
              component: () => import('@/views/sectionLeader/deviceReview/faultClear/FaceRecognition.vue'),
              name: 'FaceRecognition',
              meta: {
                title: '人脸识别验证',
                icon: 'dashboard',
                hidden: true
              },
            },
          
            
            {
              path: "inspectionAcceptance",
              component: () =>
                import(/* webpackChunkName: "inspectionAcceptance" */ "@/views/sectionLeader/deviceReview/inspectionAcceptance/index.vue"),
              meta: {
                title: "巡检验收",
                icon: "icon-dish",
              }
            },
            {
              path: "maintenanceAcceptance",
              component: () =>
                import(/* webpackChunkName: "maintenanceAcceptance" */ "@/views/sectionLeader/deviceReview/maintenanceAcceptance/index.vue"),
              meta: {
                title: "保养验收",
                icon: "icon-dish",
              }
            },
            {
              path: "testingAcceptance",
              component: () =>
                import(/* webpackChunkName: "testingAcceptance" */ "@/views/sectionLeader/deviceReview/testingAcceptance/index.vue"),
              meta: {
                title: "检测验收",
                icon: "icon-dish",
              }
            }
          ]
        },
      ]
    },
    //采购管理员
    {
      path: "/purchasingOffer",
      component: Layout,
      redirect: "/purchasingOffer/dashboard",
      children: [
        {
          path: "dashboard",
          component: () =>
            import(/* webpackChunkName: "dashboard" */ "@/views/purchasingOffer/dashboard/index.vue"),
          name: "purchasingOfferdashboard",
          meta: {
            title: "工作台",
            icon: "dashboard",
            affix: true,

          }
        },
        {
          path: "statistics",
          component: () =>
            import(/* webpackChunkName: "shopTable" */ "@/views/purchasingOffer/statistics/index.vue"),
          meta: {
            title: "数据统计",
            icon: "icon-statistics",

          }
        },
        {
