import { Vue, Component, Prop, Watch } from 'vue-property-decorator'
/* import { getOrderDetailPage } from '@/api/order' */

@Component({
  name: 'TabChange'
})
export default class extends Vue {
  @Prop({ default: '' }) orderStatics: any
  @Prop({ default: '' }) defaultActivity: any
  private activeIndex: number = this.defaultActivity || 0

  @Watch('defaultActivity')
  private onChange(val) {
    this.activeIndex = Number(val)
  }

  get changedOrderList() {
  return [
    {
      label: '全部审核单',
      value: 0,
      num: this.orderStatics.pendingReview
    },
    {
      label: '待班组长审核',
      value: 1,
      num: this.orderStatics.teamLeaderReview
    },
    {
      label: '待工区长审核',
      value: 2,
      num: this.orderStatics.sectionLeaderReview
    },
    {
      label: '待主任签字',
      value: 3,
      num: this.orderStatics.directorSign
    },
    {
      label: '已完成',
      value: 4,
      num: this.orderStatics.completed
    }
  ]
}

  private tabChange(activeIndex) {
    this.activeIndex = activeIndex
    this.$emit('tabChange', activeIndex)
  }
}
